// src/App.js

import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import io from 'socket.io-client';
import Editor from '@monaco-editor/react';
import SimpleEditor from 'react-simple-code-editor';
import Prism from 'prismjs';
import 'prismjs/themes/prism.css';
import 'prismjs/components/prism-python';
import 'prismjs/components/prism-javascript';

const LANGUAGES = [
  { label: 'Python', value: 'python', monaco: 'python' },
  { label: 'JavaScript', value: 'javascript', monaco: 'javascript' },
  { label: 'C', value: 'c', monaco: 'c' },
];

const SERVER_PORT = 3001;
const SERVER_URL = `${window.location.protocol}//${window.location.hostname}:${SERVER_PORT}`;
const socket = io(SERVER_URL);
const ROOM = 'default';

export default function App() {
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [running, setRunning] = useState(false);
  const [files, setFiles] = useState([]);
  const [language, setLanguage] = useState('python');
  const [dragging, setDragging] = useState(false);

  const editorRef = useRef(null);
  const fileInputRef = useRef(null); // Added null initialization
  const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);

  const langConfig = LANGUAGES.find(l => l.value === language) || LANGUAGES[0];

  // Ref to hold the current code value for access in socket listeners
  const codeRef = useRef(code);
  useEffect(() => {
    codeRef.current = code;
  }, [code]);

  useEffect(() => {
    const handleInit = ({ code: initialCode, ip }) => {
      // Using `ip` from server as per original, assuming it's the intended connect address.
      // If you prefer to show the hostname the client connected to:
      // const connectAddress = `${window.location.hostname}:${SERVER_PORT}`;
      const connectAddress = `${ip}:3000`; // Or use SERVER_PORT if 3000 is specific to a different context
      setCode(`# Connect at http://${connectAddress}\n\n` + (initialCode || ''));
    };

    const handleCodeUpdate = (updatedCode) => {
      // Only update if the received code is different from the current local code.
      // This prevents overwriting local edits if server echoes changes or if updates are redundant.
      if (updatedCode !== codeRef.current) {
        setCode(updatedCode);
      }
    };

    const handleOutput = (data) => {
      setOutput(prev => prev + data);
    };

    const handleDone = () => {
      setRunning(false);
    };

    const handleFileList = (list) => {
      setFiles(list);
    };

    socket.emit('join', ROOM);
    socket.on('init', handleInit);
    socket.on('code', handleCodeUpdate);
    socket.on('output', handleOutput);
    socket.on('done', handleDone);
    socket.on('file-list', handleFileList);

    return () => {
      // Clean up specific listeners
      socket.off('init', handleInit);
      socket.off('code', handleCodeUpdate);
      socket.off('output', handleOutput);
      socket.off('done', handleDone);
      socket.off('file-list', handleFileList);
      // socket.disconnect(); // Disconnect if App component truly unmounts and connection shouldn't persist.
                           // For a single-page app where App is root, this is often fine.
    };
  }, [ROOM]); // ROOM is a constant, so effect runs once on mount.

  const handleEditorChange = (value) => {
    const newCode = value || ''; // Ensure code is always a string
    setCode(newCode);
    socket.emit('code', { room: ROOM, value: newCode });
  };

  const handleEditorDidMount = (editorInstance) => { // Renamed 'editor' to 'editorInstance' for clarity
    editorRef.current = editorInstance;
    const resizeListener = () => {
      if (editorRef.current) {
        editorRef.current.layout();
      }
    };
    window.addEventListener('resize', resizeListener);

    // Note: If the Editor component itself can unmount independently of the App component
    // (e.g., if isMobile could change and swap editors), this listener would need cleanup.
    // For instance, return () => window.removeEventListener('resize', resizeListener);
    // from a useEffect hook that sets up this specific editor.
    // However, given onMount, cleanup is trickier. If App unmounts, socket cleanup handles global concerns.
  };

  const runCode = () => {
    setOutput('');
    setRunning(true);
    socket.emit('compile', { room: ROOM, code, language });
  };

  const uploadFile = async (file) => {
    if (!file) return; // Guard against null file
    const form = new FormData();
    form.append('file', file);
    try {
      await fetch(`${SERVER_URL}/upload`, { method: 'POST', body: form });
      if (fileInputRef.current) fileInputRef.current.value = ''; // Clear file input
    } catch (error) {
      console.error('File upload failed:', error);
      setOutput(prev => prev + `File upload failed: ${error.message}\n`);
    }
  };

  const clearFiles = async () => {
    try {
      await fetch(`${SERVER_URL}/clear-files`, { method: 'DELETE' });
      setOutput(''); // Or a success message
      setFiles([]); // Also clear local file list state
    } catch (error) {
      console.error('Clear files failed:', error);
      setOutput(prev => prev + `Clearing files failed: ${error.message}\n`);
    }
  };

  const handleDrag = (e, state) => {
    e.preventDefault();
    e.stopPropagation(); // Good practice for drag events
    setDragging(state);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation(); // Good practice
    setDragging(false);
    if (e.dataTransfer.files && e.dataTransfer.files.length > 0) {
      const file = e.dataTransfer.files[0];
      uploadFile(file);
      e.dataTransfer.clearData(); // Clean up
    }
  };

  return (
    <div className="container">
      <div className="top-split">
        <div className="editor-pane">
          <div className="controls">
            <select value={language} onChange={e => setLanguage(e.target.value)}>
              {LANGUAGES.map(l =>
                <option key={l.value} value={l.value}>{l.label}</option>
              )}
            </select>
          </div>

          {isMobile ? (
            <SimpleEditor
              value={code}
              onValueChange={handleEditorChange} // Assuming SimpleEditor also calls with just the value
              highlight={c => Prism.highlight(c, Prism.languages[language] || Prism.languages.python, language)}
              padding={12}
              style={{ width: '100%', height: '100%', fontFamily: 'monospace', fontSize: 14 }}
              // Consider adding a readOnly prop if `running` is true for SimpleEditor too
            />
          ) : (
            <Editor
              height="100%" // Ensure .editor-pane and its parents have defined heights
              language={langConfig.monaco}
              value={code}
              onChange={handleEditorChange}
              onMount={handleEditorDidMount}
              theme="vs-dark"
              options={{
                automaticLayout: false, // disable Monaco's ResizeObserver, as requested
                readOnly: running,      // Optionally make editor read-only while code is running
                // Add any other Monaco options you need
              }}
            />
          )}
        </div>

        <div className="output-pane">
          <pre>{output}</pre>
        </div>
      </div>

      <div className="controls">
        <button onClick={runCode} disabled={running}>
          {running ? '…Running' : 'Run'}
        </button>
        <input type="file" ref={fileInputRef} onChange={e => uploadFile(e.target.files && e.target.files[0])} style={{ marginLeft: '10px', marginRight: '10px' }}/>
        <button onClick={clearFiles}>Clear Uploads</button>
      </div>

      <div
        className={`shared-files${dragging ? ' drag-over' : ''}`}
        onDragOver={e => handleDrag(e, true)}
        onDragEnter={e => handleDrag(e, true)} // Handle entering the drop zone
        onDragLeave={e => handleDrag(e, false)}
        onDrop={handleDrop}
      >
        <h4>Shared Files</h4>
        {files.length === 0 && !dragging && <p>No files shared yet.</p>}
        <ul>
          {files.map((f, i) =>
            <li key={f.originalname + i}> {/* Use a more stable key if possible */}
              <a href={`${SERVER_URL}${f.url}`} download={f.originalname}>{f.originalname}</a>
            </li>
          )}
        </ul>
        <p className="drop-hint">
          {dragging ? 'Release to upload' : (files.length > 0 ? 'Drag & drop another file or use upload button.' : 'Drag & drop a file here, or use the upload button above')}
        </p>
      </div>
    </div>
  );
}
