import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import io from 'socket.io-client';
import Editor from '@monaco-editor/react';
import SimpleEditor from 'react-simple-code-editor';
import { highlight, languages } from 'prismjs/components/prism-core';
import 'prismjs/components/prism-python';
import 'prismjs/components/prism-javascript'; // Added for JavaScript
import 'prismjs/components/prism-clike'; // Added for C
import 'prismjs/themes/prism.css';

const SERVER_PORT = 3001;
const SERVER_URL = `${window.location.protocol}//${window.location.hostname}:${SERVER_PORT}`;
const socket = io(SERVER_URL);
const ROOM = 'default';

const languageOptions = [
  { value: 'python', label: 'Python', prismLang: 'python', monacoLang: 'python', comment: '# Write Python code here' },
  { value: 'javascript', label: 'JavaScript', prismLang: 'javascript', monacoLang: 'javascript', comment: '// Write JavaScript code here' },
  { value: 'c', label: 'C', prismLang: 'clike', monacoLang: 'c', comment: '// Write C code here' },
  // You could add more languages here, e.g. cpp, java etc.
  // { value: 'cpp', label: 'C++', prismLang: 'clike', monacoLang: 'cpp', comment: '// Write C++ code here' },
];

export default function App() {
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [running, setRunning] = useState(false);
  const [files, setFiles] = useState([]);
  const [dragging, setDragging] = useState(false);
  const [selectedLanguage, setSelectedLanguage] = useState(languageOptions[0].value); // Default to Python
  const fileInputRef = useRef();
  const isMobile = /iPhone|iPad|iPod|Android/i.test(navigator.userAgent);

  useEffect(() => {
    socket.emit('join', ROOM);

    socket.on('init', ({ code: initial, ip }) => {
      const currentLangConfig = languageOptions.find(lang => lang.value === selectedLanguage) || languageOptions[0];
      setCode(
        `# Connect at http://${ip}:3000\n# Language: ${currentLangConfig.label}\n\n` +
        initial.replace(/^# Write Python code here/i, currentLangConfig.comment) // Make initial replacement more generic or based on server knowledge
      );
    });
    socket.on('code', updated => setCode(updated));
    socket.on('output', data => setOutput(prev => prev + data));
    socket.on('done', () => setRunning(false));
    socket.on('file-list', list => setFiles(list));

    return () => {
      socket.off('init');
      socket.off('code');
      socket.off('output');
      socket.off('done');
      socket.off('file-list');
      socket.disconnect();
    };
  }, []); // Rerun init logic if selectedLanguage changes and you want server to send new boilerplate

  const handleLanguageChange = (newLanguageValue) => {
    setSelectedLanguage(newLanguageValue);
    const newLangConfig = languageOptions.find(lang => lang.value === newLanguageValue);
    // Optionally, clear code or set a default template for the new language
    // For now, we'll just update the language, existing code remains.
    // You could also emit an event to the server if language change needs to be synced.
    if (newLangConfig) {
        // A simple way to change the initial comment if the code is mostly placeholder
        // This is a basic example; more robust logic might be needed
        const placeholderRegex = /(# Write Python code here|# Language:.*|\/\/ Write JavaScript code here|\/\/ Write C code here)/i;
        if (placeholderRegex.test(code) || code.trim() === `Connect at http://${window.location.hostname}:3000\n\n`) {
             const initialComment = `# Connect at http://${window.location.hostname}:3000\n# Language: ${newLangConfig.label}\n\n${newLangConfig.comment}`;
             setCode(initialComment);
             socket.emit('code', { room: ROOM, value: initialComment });
        }
    }
  };

  const handleEditorChange = (value) => {
    setCode(value);
    socket.emit('code', { room: ROOM, value });
  };

  const runCode = () => {
    setOutput('');
    setRunning(true);
    // Send the selected language to the backend
    socket.emit('compile', { room: ROOM, language: selectedLanguage });
  };

  const uploadFile = async (file) => {
    const form = new FormData();
    form.append('file', file);
    try {
      await fetch(`${SERVER_URL}/upload`, { method: 'POST', body: form });
      if (fileInputRef.current) fileInputRef.current.value = '';
    } catch (err) {
      console.error('Upload failed', err);
    }
  };

  const onFileInputChange = (e) => {
    const file = e.target.files[0];
    if (file) uploadFile(file);
  };

  const clearFiles = async () => {
    try {
      await fetch(`${SERVER_URL}/clear-files`, { method: 'DELETE' });
      setOutput(''); // Clear the output panel
    } catch (err) {
      console.error('Clear files failed', err);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragging(true);
  };
  const handleDragLeave = (e) => {
    e.preventDefault();
    setDragging(false);
  };
  const handleDrop = (e) => {
    e.preventDefault();
    setDragging(false);
    const file = e.dataTransfer.files[0];
    if (file) uploadFile(file);
  };

  const currentLanguageConfig = languageOptions.find(lang => lang.value === selectedLanguage) || languageOptions[0];

  return (
    <div className="container">
      <div className="top-split">
        <div className="editor-pane">
          {isMobile ? (
            <SimpleEditor
              value={code}
              onValueChange={handleEditorChange}
              highlight={c => highlight(c, languages[currentLanguageConfig.prismLang] || languages.plaintext, currentLanguageConfig.prismLang)}
              padding={12}
              style={{
                width: '100%',
                height: '100%',
                fontFamily: 'monospace',
                fontSize: 14,
                border: '1px solid #ddd' // Added for better visibility
              }}
            />
          ) : (
            <Editor
              height="100%"
              language={currentLanguageConfig.monacoLang}
              value={code}
              onChange={handleEditorChange}
              theme="vs-dark"
            />
          )}
        </div>
        <div className="output-pane">
          <pre>{output}</pre>
        </div>
      </div>

      <div className="controls">
        <select 
          value={selectedLanguage} 
          onChange={(e) => handleLanguageChange(e.target.value)}
          style={{ marginRight: '10px', padding: '5px' }}
        >
          {languageOptions.map(lang => (
            <option key={lang.value} value={lang.value}>
              {lang.label}
            </option>
          ))}
        </select>
        <button onClick={runCode} disabled={running}>
          {running ? 'â€¦Running' : 'Run'}
        </button>
        <input type="file" style={{display: 'none'}} ref={fileInputRef} onChange={onFileInputChange} />
         <button onClick={() => fileInputRef.current && fileInputRef.current.click()} style={{ marginLeft: '10px' }}>
          Select File
        </button>
        <button
          onClick={() =>
            fileInputRef.current?.files[0] &&
            uploadFile(fileInputRef.current.files[0])
          }
          disabled={!fileInputRef.current?.files?.length} // Disable if no file selected
        >
          Upload File
        </button>
        <button onClick={clearFiles} style={{ marginLeft: '10px' }}>
          Clear Uploads
        </button>
      </div>

      <div
        className={`shared-files${dragging ? ' drag-over' : ''}`}
        onDragOver={handleDragOver}
        onDragLeave={handleDragLeave}
        onDrop={handleDrop}
      >
        <h4>Shared Files</h4>
        <ul>
          {files.map((f, i) => (
            <li key={i}>
              <a href={`${SERVER_URL}${f.url}`} download>
                {f.originalname}
              </a>
            </li>
          ))}
        </ul>
        <p className="drop-hint">
          {dragging
            ? 'Release to upload'
            : 'Drag & drop a file here, or use the "Select File" button'}
        </p>
      </div>
    </div>
  );
}
