import React, { useState, useEffect, useRef } from 'react';
import Editor from '@monaco-editor/react';
import io from 'socket.io-client';

const SERVER_PORT = 3001;
const SERVER_URL = `${window.location.protocol}//${window.location.hostname}:${SERVER_PORT}`;
const socket = io(SERVER_URL);
const ROOM = 'default';

export default function App() {
  const [code, setCode] = useState('');
  const [output, setOutput] = useState('');
  const [running, setRunning] = useState(false);
  const editorRef = useRef(null);

  useEffect(() => {
    socket.emit('join', ROOM);

    socket.on('init', ({ code }) => setCode(code));
    socket.on('code', updated => setCode(updated));
    socket.on('output', data => setOutput(prev => prev + data));
    socket.on('done', () => setRunning(false));

    return () => socket.disconnect();
  }, []);

  const handleEditorChange = (value) => {
    setCode(value);
    socket.emit('code', { room: ROOM, value });
  };

  const handleEditorMount = (editor) => {
    editorRef.current = editor;
    setTimeout(() => editor.layout(), 100); // give layout a chance to adjust
    window.addEventListener('resize', () => editor.layout());
  };

  const runCode = () => {
    setOutput('');
    setRunning(true);
    socket.emit('compile', { room: ROOM, code });
  };

  return (
    <div style={{ height: '100vh', display: 'flex', flexDirection: 'column' }}>
      <div style={{ flexGrow: 1 }}>
        <Editor
          value={code}
          onChange={handleEditorChange}
          onMount={handleEditorMount}
          language="python"
          theme="vs-dark"
          options={{
            automaticLayout: false,
            minimap: { enabled: false },
          }}
        />
      </div>
      <div style={{ padding: '10px', background: '#111', color: '#0f0' }}>
        <button onClick={runCode} disabled={running}>
          {running ? 'Running...' : 'Run'}
        </button>
        <pre>{output}</pre>
      </div>
    </div>
  );
}
